import{_ as e,a as c,b as d,c as t,d as r,e as s,f as a,g as p,h as n,i}from"./chunks/image718.CR0IlQud.js";import{_ as g,c as l,a2 as _,o as h}from"./chunks/framework.DvpCT43G.js";const P=JSON.parse('{"title":"63. «Дикие» завершители предложений в реальном японском: かい, だい, ぜ, ぞ, さ, から, し, ちょうだい","description":"","frontmatter":{},"headers":[],"relativePath":"ru/63-wild-sentence-enders-in-real-life-japanese-かい-だい-ぜ-ぞ-さ-から-し-ちょうだい.md","filePath":"ru/63-wild-sentence-enders-in-real-life-japanese-かい-だい-ぜ-ぞ-さ-から-し-ちょうだい.md","lastUpdated":1754029915000}'),m={name:"ru/63-wild-sentence-enders-in-real-life-japanese-かい-だい-ぜ-ぞ-さ-から-し-ちょうだい.md"};function u(f,o,b,k,q,w){return h(),l("div",null,o[0]||(o[0]=[_('<h1 id="_63-«дикие»-завершители-предложении-в-реальном-японском-かい-だい-ぜ-ぞ-さ-から-し-ちょうだい" tabindex="-1"><strong>63. «Дикие» завершители предложений в реальном японском: かい, だい, ぜ, ぞ, さ, から, し, ちょうだい</strong> <a class="header-anchor" href="#_63-«дикие»-завершители-предложении-в-реальном-японском-かい-だい-ぜ-ぞ-さ-から-し-ちょうだい" aria-label="Permalink to &quot;**63. «Дикие» завершители предложений в реальном японском: かい, だい, ぜ, ぞ, さ, から, し, ちょうだい**&quot;">​</a></h1><p><a href="https://www.youtube.com/watch?v=CVJ4jTlxyno&amp;list=PLg9uYxuZf8x_A-vcqqyOFZu06WlhnypWj&amp;index=65&amp;pp=iAQB" target="_blank" rel="noreferrer"><strong>«Дикие» завершители предложений в реальном японском: かい, だい, ぜ, ぞ, さ, から, し, ちょうだい | Урок 63</strong></a></p><p>こんにちは。</p><p>Сегодня мы поговорим о тех вещах, которые бьют нас между глаз, как только мы покидаем огороженный сад теоретического японского и отправляемся в дикие леса настоящего языка. Один из вопросов, который мне часто задают, касается различных завершителей предложений, с которыми люди сталкиваются. И я говорю не о «ручных» вроде <code>ね</code>, <code>よ</code> и <code>な</code> – если вам интересно, я сделала <a href="https://www.youtube.com/watch?v=IWEok4Ivfyc" target="_blank" rel="noreferrer"><strong>видео</strong></a> об этом, и я помещу ссылку на него над своей головой.</p><p>Но сегодня мы поговорим о некоторых более разговорных и нетрадиционных завершителях предложений, с которыми вы столкнетесь, как только погрузитесь в «дикий японский» в аниме, манге и так далее.</p><h2 id="かい" tabindex="-1">かい <a class="header-anchor" href="#かい" aria-label="Permalink to &quot;かい&quot;">​</a></h2><p>Итак, один из тех, о которых меня часто спрашивают, это <code>かい</code>, и <code>かい</code> — это просто смягченная и разговорная форма <code>か</code>. Как мы знаем, <code>か</code> регулярно используется в формальном японском, где мы используем украшения です/ます в конце предложений, чтобы формировать вопросы. Так мы говорим <code>何々しますか / 何々ですか</code>.</p><p>Но в разговорном японском мы не так уж часто используем <code>か</code> в качестве вопросительного маркера в конце предложения. <strong>Мы используем его в других позициях</strong> — и я сделала видео о различных способах использования <code>か</code><sup><a href="./39-the-か-particle-buried-questions-かな-もんか-かどうか">[39]</a></sup>, и я тоже помещу ссылку на него над своей головой. Но там, где мы, как правило, не используем его в неформальной речи, в целом, это в конце предложений, отмечающих вопросы, так, как это делается в です/ます японском.</p><p>И это не потому, что это неграмматично, на самом деле это совершенно грамматично, но в современном японском это стало звучать немного грубо, поэтому, хотя вы можете услышать это между мужчинами, которые ведут себя как «свои парни», вы не так часто услышите это в женской речи или в речи любого, кто пытается быть немного вежливым. Так что же нам делать? Большую часть времени мы просто используем восходящую интонацию в речи или вопросительный знак в письменной речи, когда изображаем непринужденную речь. И в этом нет ничего плохого.</p><p>Испанский постоянно задает вопросы, просто добавляя восходящую интонацию к эквивалентному утверждению, и там нет никаких проблем. В японском тоже нет проблем. Иногда мы добавляем <code>の</code>, что неоднозначно, потому что оно может отмечать как вопрос, так и утверждение, но опять же, это не имеет значения, потому что интонация работает так же хорошо.</p><p><img src="'+e+'" alt=""></p><p>Однако у нас есть неформальный, негрубый вопросительный маркер <code>かい</code>. Вы можете подумать, что его будут использовать постоянно, <strong>но это не так</strong>, потому что он звучит очень разговорно и иногда немного... ну, простонародно. Некоторые источники говорят, что он в основном мужской, и это отчасти правда. Вы не услышите, чтобы его использовали многие молодые женщины, но иногда его используют пожилые женщины.</p><h2 id="だい-どうだい" tabindex="-1">だい (&amp; どうだい) <a class="header-anchor" href="#だい-どうだい" aria-label="Permalink to &quot;だい (&amp; どうだい)&quot;">​</a></h2><p>У <code>かい</code> также есть партнер — <code>だい</code>.</p><p><strong><code>だい</code> — это на самом деле <code>だ</code> плюс <code>かい</code>.</strong></p><p>Так что это форма <code>かい</code>, которую мы используем в предложениях, оканчивающихся на связку, в предложениях, оканчивающихся на <code>だ</code>.</p><p><img src="'+c+'" alt=""></p><p>И почти все, что я сказала о <code>かい</code>, также относится к <code>だい</code>, <strong>за исключением</strong> того, что существует особое устойчивое сочетание <code>どうだい</code>, которое используется гораздо шире. Итак, если кто-то ест что-то, и вы говорите <code>どうだい?</code> — <code>как это? / каково это?</code> — и они могут сказать <code>おいしい/美味しい!</code> или они могут сказать <code>まずい/不味い</code>, но <strong>спросить в такой форме может любой, при условии, что это неформальная обстановка.</strong></p><h2 id="ちょうだい" tabindex="-1">ちょうだい <a class="header-anchor" href="#ちょうだい" aria-label="Permalink to &quot;ちょうだい&quot;">​</a></h2><p>Люди также сталкиваются с выражением <code>ちょうだい</code>, <strong>и оно никак не связано с <code>かい</code> или <code>だい</code>;</strong> <strong>это неформальный эквивалент <code>ください</code>.</strong> Так что обычно оно добавляется к て-форме глагола, чтобы попросить кого-то что-то сделать. Иногда считается, что это женская речь, <strong>но это не исключительно так.</strong></p><p><img src="'+d+'" alt=""></p><h2 id="ぜ-ぞ-さ" tabindex="-1">ぜ, ぞ, さ <a class="header-anchor" href="#ぜ-ぞ-さ" aria-label="Permalink to &quot;ぜ, ぞ, さ&quot;">​</a></h2><p>Другие настоящие маркеры завершителей предложений в непринужденной речи — это <code>ぜ</code>, <code>ぞ</code> и <code>さ</code>. Итак, <code>ぜ</code> и <code>ぞ</code> очень просты. Это просто словесные восклицательные знаки, которые придают немного силы тому, что вы говорите. <strong>Они довольно грубые и действительно являются мужской речью, и это то, что вы, вероятно, увидите</strong> <strong>больше в аниме и манге, чем в реальной жизни.</strong></p><p><img src="'+t+'" alt=""></p><p><code>さ</code> похожа, но она не так груба. <strong>Она гораздо шире используется</strong>, вы увидите ее много, и вы услышите ее в реальной жизни. Ее обычно называют мужской речью, но опять же, <strong>это не исключительно мужская речь</strong>. Ее используют самые разные люди.</p><p><strong>Важно не путать <code>さ</code>, завершитель предложения, придающий акцент,</strong> <strong>с <code>さあ</code>, которое стоит в начале предложения и является скорее выражением, дающим время на размышление.</strong></p><hr><p>Теперь, есть также завершители предложений, <strong>которые не являются завершителями предложений, но фактически ими являются</strong>, потому что люди ставят их после локомотива в конце предложения.</p><p><img src="'+r+'" alt=""> Здесь Долли также предлагает посмотреть это <a href="https://www.youtube.com/watch?v=Au5JOtcwE7A&amp;ab_channel=OrganicJapanesewithCureDolly" target="_blank" rel="noreferrer"><strong>видео</strong></a> о псевдо-завершителях, のに и なのに соответственно.</p><p>Один из них — <code>から</code>.</p><h2 id="から-завершитель-предложения" tabindex="-1">から <code>завершитель предложения</code> <a class="header-anchor" href="#から-завершитель-предложения" aria-label="Permalink to &quot;から &lt;code&gt;завершитель предложения&lt;/code&gt;&quot;">​</a></h2><p>Итак, <code>から</code>, как вы знаете, <strong>технически не является завершителем предложения.</strong> <strong>Это союз, который соединяет два логических придаточных предложения, образуя сложное предложение.</strong> Так, правильное использование <code>から</code> может быть: <code>さくらが来ないから帰ります</code>, что означает <code>Поскольку Сакура не приходит, я иду домой / Сакура не приходит, поэтому я ухожу</code>.</p><p>Однако вы можете просто сказать, уходя: <code>さくらが来ないから</code>, и это просто оставляет вторую половину предложения недосказанной, потому что она очевидна. Теперь, исходя из этого, <code>から</code> может быть добавлено ко многим предложениям, чтобы подразумевать, что что-то еще последует.</p><p><strong>Это дополнительное «что-то еще» не всегда полностью ясно.</strong> На самом деле, <code>から</code> может выполнять функцию либо смягчения того, что оно отмечает, либо его ужесточения.</p><p><img src="'+s+'" alt=""></p><p>Оно может смягчить его, делая утверждение объяснительным; оно может ужесточить его, заставляя звучать так, будто это часть логического аргумента или естественного рассуждения, которое слушатель должен понять и с которым должен согласиться.</p><p>Например, если вы едете на велосипеде там, где нельзя ездить на велосипеде, и к вам подходит какой-то официальный человек и говорит <code>自転車に乗るのは禁止ですから</code> [вежливый тон], это примирительно, и на самом деле все, что они хотят сказать, это <code>Здесь запрещено ездить на велосипеде</code>, но, добавляя <code>から</code> в конец, они как бы смягчают это, превращая в объяснение для вас.</p><p><img src="'+a+'" alt=""></p><p>И наоборот, они могут сказать <code>自転車に乗るのは禁止ですから</code> [строгий тон], и это имеет другое значение. Здесь <code>禁止</code> вбивается с ещё большей силой благодаря этому <code>から</code>.</p><p><img src="'+p+'" alt=""></p><p>Таким образом, <code>から</code> может иметь различные применения, часто зависящие от контекста, от того, как они произносятся, от манеры говорящего и от всей ситуации.</p><h2 id="し-завершитель-предложения" tabindex="-1">し завершитель предложения <a class="header-anchor" href="#し-завершитель-предложения" aria-label="Permalink to &quot;し завершитель предложения&quot;">​</a></h2><p><code>し</code> — еще одна очень распространенная частица, завершающая предложение, которая похожа на <code>から</code> и имеет значение в той же области. <code>し</code> может фактически объединять различные условия или утверждения, <strong>но ее наиболее распространенное и регулярное использование — это объединение причин.</strong></p><p>Так, например, мы можем сказать <code>遅くなったしさくらが来ないし帰ります</code>, и это означает «Поскольку стало поздно и поскольку Сакура не приходит, я иду домой». И <strong>это наиболее стандартное использование <code>し</code>.</strong></p><hr><p>Но затем мы можем часто использовать его только для одного условия, подразумевая другие. Например, если бы мы сказали <code>遅くなったし帰ります</code>, мы говорим <code>поскольку стало поздно, я иду домой</code>, <strong>но мы также подразумеваем, что тот факт, что стало поздно, не единственная причина:</strong> <code>Поскольку стало поздно (и по другим причинам) я иду домой.</code></p><p>И это, в данном конкретном случае, вероятно, подразумевает, что это не только потому, что становится поздно, <strong>это также потому, что Сакура не приходит, но мы оставляем недосказанной более личную часть этого.</strong></p><p>Теперь, наоборот, мы можем сказать <code>さくらが来ないし帰ります</code>, и на этот раз мы вставили самую важную часть, настоящую причину нашего ухода, но, сказав <code>し</code>, а не <code>から</code>, мы также говорим: <code>Поскольку Сакура не приходит (и по другим причинам) я ухожу.</code></p><p><strong>Что это делает, так это снимает давление с Сакуры.</strong> Хотя она может быть главной причиной, мы немного рассеиваем это, предполагая, что есть и другие причины. <strong>И мы можем сделать это, когда других причин вообще нет,</strong> <strong>просто как способ смягчить и рассеять утверждение.</strong></p><p>Другой пример: предположим, вы снова едете на велосипеде в том месте, где нельзя ездить, и на этот раз к вам подходит друг и говорит: <code>自転車に乗るのは禁止し</code>.</p><p><img src="'+n+'" alt=""></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Да, обычно после 禁止 должно быть だ перед し. Но поскольку это непринужденная речь, оно опущено.</p></div><p>Теперь, <strong>разница между этим и утверждением с <code>から</code></strong> <strong>заключается просто в том, что <code>し</code> смягчает его.</strong> Оно говорит: «О, потому что здесь запрещено ездить на велосипеде, и по другим причинам...»</p><p><strong>Теперь, никаких «других причин» вообще нет, но</strong> <strong>это делает речь более разговорной, более дружелюбной</strong>, и немного размывает то <code>から</code>, которое может звучать слишком похоже на приказ. Оно просто говорит: «О, по этой и другим причинам».</p><p>И эта тактика «размывания» часто встречается в языках. В английском это часто делается путем добавления слов вроде <code>like</code> или <code>kinda</code> к тому, что вы говорите.</p><p>Теперь, способы, которыми работают эти завершители предложений, нельзя выучить только из структуры или только из абстрактных инструкций. <strong>Нам нужно погрузиться в японский, чтобы почувствовать,</strong> <strong>что люди имеют в виду, когда говорят эти вещи.</strong></p><p>Но, наоборот, понимание структуры, понимание того, как они на самом деле сочетаются, дает нам существенный толчок в способности осмыслить наше погружение. Это похоже на веревку и восхождение на гору.</p><p><img src="'+i+'" alt=""></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Действительно, это немного изменено... как-то... *кхе-кхе*... странно... ╮(︶▽︶)╭ <em>Если у вас есть веревка, это поможет вам взобраться на гору,</em></p></div><p>но если вы просто стоите, уставившись и изучая все о веревке, вы никогда не доберетесь до вершины горы. И наоборот, если вы попытаетесь взобраться на гору без веревки, вы можете не продвинуться очень далеко...</p>',60)]))}const y=g(m,[["render",u]]);export{P as __pageData,y as default};
